    /// <summary>
    /// Registry for all conversation definitions.
    /// Provides O(1) lookup for conversations and supports conditional/dynamic text.
    /// </summary>
    public static class ConversationRegistry
    {
        private static Dictionary<string, ConversationDefinition> s_Conversations;
        
        static ConversationRegistry()
        {
            s_Conversations = new Dictionary<string, ConversationDefinition>();
            InitializeConversations();
        }
        
        /// <summary>
        /// Gets conversation text for the specified conversation type
        /// </summary>
        /// <param name="conversationType">The conversation type identifier</param>
        /// <param name="myName">The speaker's name</param>
        /// <param name="yourName">The listener's name</param>
        /// <returns>The formatted conversation text</returns>
        public static string GetConversationText(string conversationType, string myName, string yourName)
        {
            ConversationDefinition definition;
            if (!s_Conversations.TryGetValue(conversationType, out definition))
            {
                return string.Empty;
            }
            
            // Handle dynamic text generators (like Jester)
            if (definition.DynamicTextGenerator != null)
            {
                return definition.DynamicTextGenerator(myName, yourName);
            }
            
            // Build text from base and conditionals
            string text = definition.BaseText;
            
            foreach (var conditional in definition.ConditionalTexts)
            {
                if (conditional.Condition != null && conditional.Condition())
                {
                    if (conditional.DynamicConditionalText != null)
                    {
                        text += conditional.DynamicConditionalText();
                    }
                    else
                    {
                        text += conditional.ConditionalText;
                    }
                }
            }
            
            // Replace placeholders
            text = text.Replace("{MY_NAME}", myName);
            text = text.Replace("{YOUR_NAME}", yourName);
            text = text.Replace("{SERVER_NAME}", Server.Misc.ServerList.ServerName);
            
            return text;
        }
        
        /// <summary>
        /// Registers a new conversation type
        /// </summary>
        /// <param name="conversationType">The conversation type identifier</param>
        /// <param name="definition">The conversation definition</param>
        public static void RegisterConversation(string conversationType, ConversationDefinition definition)
        {
            s_Conversations[conversationType] = definition;
        }
        
